import React from 'react';
import { ButtonSmall, Flex, NormalFont, Grid } from 'components/Styles';
import styled from 'styled-components';
import thumbnail from 'assets/img/thumbnail.png';
import bookmark from 'assets/img/bookmark.svg';
import time from 'assets/img/time.svg';
import Course from 'components/Course';

const Wrapper = styled.div`
    padding-top: 20px;
    padding-left: 25px;
	padding-right: 33px;
`

const TopContainer = styled.div`
    display: flex;
    justify-content: space-between;
    width: 95%;
    margin: auto;
`

const SideA = styled.div`
    display: flex;
    width: 60%;
`

const About = styled.div

const SimilarCourses = styled.div`
    width: 35%;
    background-color: green;
`

const Small = styled.p`
    font-size: 0.79rem;
    margin-bottom: 25px;
    margin-top: 5px;
`

const H3 = styled.h3`
	font-weight: 600;
	font-family: 'GTWalsheimPro';
	font-size: 1.3rem;
`

const CourseDetails = () => {
    return (
        <Wrapper>
            <TopContainer>
                <SideA>
                    <img src={thumbnail} alt=""/>
                    <div style={{marginLeft:'10px'}}>
                        <H3>Advanced Neurobiology</H3>
                        <Small>ProfessorYan Zhang</Small>
                        <Flex>
                            <ButtonSmall style={{width:'120px'}}>Enroll</ButtonSmall>
                            <img src={bookmark} />
                            <img src={time} />
                        </Flex>
                    </div>

                </SideA>

                <SimilarCourses>
                    <NormalFont>Similar Courses</NormalFont>
                </SimilarCourses>
            </TopContainer>




        </Wrapper>
    );
};

export default CourseDetails;
